///- Class declaration ------------------------------------------------------------------#pragma mark GCRosterItemclass GCRosterItem{	//ÊData	protected:		std::string			mNick;		PresenceStatus		mStatus;		std::string			mFullStatus;		KarmaStatus			mKarma;		mutable bool		mIdle;		mutable long		mLastActivityTime;			// Functions	public:		explicit GCRosterItem();		explicit GCRosterItem(const std::string &inNick);		explicit GCRosterItem(const PresenceMsg &inPresenceMsg);		~GCRosterItem();				const std::string&	GetNick() const						{ return mNick; }		bool				OnActivity() const;		bool				OnIdle() const;				void				Draw( Rect& area ) const;				operator==( const GCRosterItem& aNumericString ) const	{ return (mNick == aNumericString.mNick); }		// operator< is provided so we can be put into a std::set		operator<( const GCRosterItem& aNumericString ) const	{ return (mNick < aNumericString.mNick); }};