			FailNIL( di = new ChatDialogItem( owner, item ));			break;		case CLASS_GCRosterDialogItem:			FailNIL( di = new GCRosterDialogItem( owner, item ));			break;#endif		case CLASS_ColorDialogItem:			FailNIL( di = new ColorDialogItem( owner, item ));			break;		case 'HLST':			FailNIL( di = new ZHLDialogItem( owner, item ));			break;#ifdef TOQ		case CLASS_ZIconButtonDialogItem:			FailNIL( di = new ZIconButtonDialogItem( owner, item ));			break;#endif		default:		    break;	}		return di;}