tWindow( ZCommander* aBoss, const short windID )	: ZTextWindow( aBoss, windID, false, true ){}///--------------------------------------------------------------------------------------//	¥ Destructor:///--------------------------------------------------------------------------------------ZDebugTextWindow::~ZDebugTextWindow(){	SavePosition();}#pragma mark -///--------------------------------------------------------------------------------------//	AppendBuffer://		Append the data in buffer to our contents.//		//	Changes://		2000-04-13	MH		Created///--------------------------------------------------------------------------------------void	ZDebugTextWindow::AppendBuffer( const char * txt, long length ){	/* If we're about to exceed TextEdit's 32k limit then		delete console before appending. */	if( length > 32000 )		length = 32000;		if( (**itsText).teLength + length > 32760 )	{		TESetSelect( 0, length, itsText );		TEDelete( itsText );	}		// Now append:	Focus();	TESetSelect( 32767, 32767, itsText );	// Make sure we add to the end of the text	TEInsert( txt, length, itsText );	RecalText( true );}///--------------------------------------------------------------------------------------//	CheckForSave://		Never try to save the data when apps quits.//		//	Changes://		2000-04-13	MH		Created///--------------------------------------------------------------------------------------short	ZDebugTextWindow::CheckForSave( const short phase ){	return kCloseNoSave;}