/****************************************************************************************************			MacZoop - "the framework for the rest of us"		 ****			MacZoopCommands.h		All commands that MacZoop defines***			© 2000, Graham Cox*****************************************************************************************************/#pragma once#ifndef __MACZOOPCOMMANDS__#define	__MACZOOPCOMMANDS__/************************************************************************************************/// MacZoop reserves commands numbered 1-199 for the standard menu commands such as// New, Open, Quit, Cut, Paste, etc. Your own commands should be numbered 200 and higher. You// can use any command you wish for any purpose- MacZoop is ignorant of meanings attached to// command numbers themselves. Since the command mapping tables are built on the fly at start-up,// you are free to reorganise the menus however you want to suit your application- MacZoop will// handle any standard commands present in the default manner, but doesn't care if they're not// there, etc. This allows great flexibility in organising your application. Caveat: don't add// items to the Apple Menu unless you are prepared to override the standard command handling.// Standard commands:enum{	kNoCmd = 0,					// 0	- no command (send menuID and itemID instead)	kCmdAbout = 1,				// 1	- display the about box	kCmdNew,					// 2	- make a new document or window	kCmdOpen,					// 3	- display the open file dialog	kCmdClose,					// 4	- close the active window	kCmdCloseAll,				// 5	- close all windows	kCmdSave,					// 6	- save the active window to disk	kCmdSaveAs,					// 7	- display the save dialog	kCmdRevert,					// 8	- revert the active window to its previous saved version	kCmdPageSetup,				// 9	- display the page setup dialog	kCmdPrint,					// 10	- display the print dialog and print the contents of the active window	kCmdQuit,					// 11	- quit the application	kCmdUndo,					// 12	- undo the last task	kCmdCut,					// 13	- cut the selected item to the clipboard	kCmdCopy,					// 14	- copy the selected item to the clipboard	kCmdPaste,					// 15	- paste the clipboard contents	kCmdClear,					// 16	- clear the selected item	kCmdSelectAll,				// 17	- select everything in the active window	kCmdSelectNone,				// 18	- select nothing in the active window	kCmdPlainText,				// 19	- set plain text	kCmdBoldText,				// 20	- set bold text	kCmdItalicText,				// 21	- set italic text	kCmdUnderlineText,			// 22	- set underlined text	kCmdOutlineText,			// 23	- set outlined text	kCmdShadowText,				// 24	- set shadowed text	kCmdCondensedText,			// 25	- set condensed text	kCmdExtendedText,			// 26	- set extended text	kCmdShowHideClipboard,		// 27	- show or hide the clipboard window	kCmdDoPreferences,			// 28	- open the prefs dialog, if any	kCmdSaveACopy,				// 29	- save a copy of the current file	kCmdPrintOneCopy,			// 30	- print a single copy of the current document	kCmdSetLeftJustify,			// 31	- set text left justified	kCmdSetCentreJustify,		// 32	- set text centre justified	kCmdSetRightJustify,		// 33	- set text right justified	kCmdSetFullJustify,			// 34	- set full justify, where supported	kCmdStackWindows,			// 35	- stack windows on screen in cascade	kCmdTileWindows,			// 36	- tile windows on screen	kCmdRedo,				    // 37   - redo when multiple undo implemented	kCmd_ZoomIn,              	// 38   - zoom in	kCmd_ZoomOut,               // 39   - zoom out	kCmdSetColourBlack = 40,	// 40	- set colour to black	kCmdSetColourWhite,			// 41	- set colour to white	kCmdSetColourRed,			// 42	- red	kCmdSetColourGreen,			// 43	- green	kCmdSetColourBlue,			// 44	- blue	kCmdSetColourCyan,			// 45	- cyan	kCmdSetColourMagenta,		// 46	- magenta	kCmdSetColourYellow,		// 47	- yellow	kCmdSetColourOther,			// 48	- display colour picker	kCmd_Duplicate		= 49,	// 49	- duplicate the selected object(s)	kCmd_Invert,				// 50	- invert whatever (usually graphic)	// commands 51-65 reserved for function keys - see below	kCmd_CycleWindows	= 66,	// 66	- cycle through open windows	kCmd_OpenSelection,			// 67	- open the selection (whatever that means)	kCmd_AltOpenSelection,		// 68	- alternative open the selection	kCmd_GetInfo,				// 69	- open info for selection	kCmd_ShowHideRulers,		// 70	- show or hide the current view's rulers	kCmd_ShowHideGrid,			// 71	- show or hide the view's grid	kCmd_AlignToGrid,			// 72	- align selection to grid	kCmd_GridSnapEnable,		// 73	- align to grid interactively	kCmd_FlipHorizontal,		// 74	- flip (image) horizontally	kCmd_FlipVertical,			// 75	- flip (image) vertically	kCmd_ShowHideTooltips,		// 76	- toggle state of global tooltip visibility	kCmd_OpenMultiple			// 77	- (nav only) allow open several files at once};// commands sent by function keys:enum{    kCmdF1              = 51,   // 51	- usually mapped to Undo    kCmdF2,                     // 52	- usually mapped to Cut    kCmdF3,                     // 53	- usually mapped to Copy    kCmdF4,                     // 54	- usually mapped to Paste    kCmdF5,                     // 55    kCmdF6,                     // 56    kCmdF7,                     // 57    kCmdF8,                     // 58    kCmdF9,                     // 59    kCmdF10,                    // 60    kCmdF11,                    // 61    kCmdF12,                    // 62    kCmdF13,                    // 63    kCmdF14,                    // 64    kCmdF15                     // 65};// std commands for font size menu:enum{	kStdFontSizeBase = 100,		// 100	- can be subtracted from font size command to yield actual size	kCmdStdFontSizeOther,		// 101	- indicated "Other" font size- display font size dialog.	kCmdStdFontSize7 = 107,		// 107	- font size 7	kCmdStdFontSize9 = 109,		// 109	- font size 9	kCmdStdFontSize10,			// 110	- font size 10	kCmdStdFontSize12 = 112,	// 112	- font size 12	kCmdStdFontSize14 = 114,	// 114	- font size 14	kCmdStdFontSize18 = 118,	// 118	- font size 18	kCmdStdFontSize24 = 124,	// 124	- font size 24	kCmdStdFontSize36 = 136,	// 136	- font size 36	kCmdStdFontSize48 = 148,	// 148	- font size 48	kCmdStdFontSize60 = 160,	// 160	- font size 60	kCmdStdFontSize72 = 172		// 172	- font size 72};// NOTE: all command numbers below 200 are reserved- DO NOT USE.#endif