/****************************************************************************************************			MacZoop 2.0 - "the framework for the rest of us"		 ****			ZDefines.h			-- some handy constants, etc******			© 1996, Graham Cox*****************************************************************************************************/#pragma once#ifndef __ZDEFINES__#define __ZDEFINES__#include	"GrafMacros.h"// some basic definitions in case precompiled headers do not supply them#ifndef TRUE#define	TRUE 	true#endif#ifndef FALSE#define FALSE 	false#endif#ifndef NIL#define NIL 	NULL#endif#ifndef topLeft#define topLeft(r)	(((Point *) &(r))[0])#endif#ifndef botRight#define botRight(r)	(((Point *) &(r))[1])#endif// standard basic resource IDs, etc.enum{	kUntitledWindowID			= 128,		// resource ID of the main window template	kAppleMenuID				= 128,		// ID of the apple menu	kFileMenuID					= 129,		// ID of File menu	kEditMenuID					= 130,		// ID of Edit menu	kAboutBoxID					= 128,		// ID of default "about" box	kStdMenubarID				= 128,		// ID of the default MBAR resource for the main menu bar	kStdOSXMenubarID			= 129,		// ID of MBAR used when running under MacOS X	kMiscStrListID				= 128,		// ID of string list for many common strings	kExceptionAlertID			= 130,		// Standard error alert	kMemoryLowAlertID			= 132,		// Low memory alert	kAssertionAlertID			= 138,		// ASSERT alert	kSleepTime					= 10,		// number of ticks to give up to background programs	kStdScrollbarWidth			= 15,		// pixel width/height of normal scrollbar	kStdPopUpArrowSICN  		= 128,      // various small icons used throughout	kConfirmSaveAlertID			= 129,		// Save changes alert	kMacZoopWindowIdentifier	= 747,		// used to identify our own windows	kFromAutoKeyEvent			= 0x0040	// flags key generated by autokey event (modifiers parem)};// return values from Save Changes alert:enum{	kConfirmSave = 1,	kCloseCancel,	kCloseNoSave};// macros for extracting window/dialog object references from mac windows#define		GETZWINDOW( w )		(( WINDOWKIND( w ) == kMacZoopWindowIdentifier )? \									(ZWindow*) GetWRefCon(w) : NULL )#define     GETZWINDOWREF( w )  ((const ZWindow*) GetZWindow((WindowPtr) w ))  // handy object disposal macros									#define		FORGETOBJECT(p)		{ delete (p); (p) = NULL; }#define		FORGETTHIS()		{ delete this; }// synonyms for various macros that follow modern convention that all macros// have all upper-case identifiers#define		BOTTOMRIGHTPOINT	botRight#define		TOPLEFTPOINT		topLeft	// for compatibility with older code. It's better to use the modern variant because it's// much easier to see it's a macro rather than a function.#define		ForgetObject		FORGETOBJECT#define		ForgetThis			FORGETTHIS#define		GetZWindow			GETZWINDOW#define		GetZWindowRef		GETZWINDOWREF		#define		IS_ZWINDOW_KIND		kMacZoopWindowIdentifier// common useful macros. Be careful passing functions to these, you may get poor// performance and/or incorrect results.#define		MIN( a, b )		(((a) < (b))? (a) : (b))#define		MAX( a, b )		(((a) > (b))? (a) : (b))#define		ABS( x )		(((x) < 0 )?  -(x) : (x))#define		CMP( a, b )		(((a) < (b))? -1 : (((a) > (b))? 1 : 0 ))#define		SGN( a )		(((a) < 0 )? -1 : 1 )#define		AVG( a, b )		(((a) + (b)) / 2 )#define		CENTRE	AVG#define		CENTER	CENTRE// macros for compile-time string hacking:#define		CLITERAL(x)		#x#define		PLITERAL(x)		CLITERAL(\p##x)#if defined(__SC__) && (defined(MPW_CPLUS) || defined(MPW_C))#define		HEADER(x)		CLITERAL(x##.##h)#else#define		HEADER(x)		CLITERAL(x.h)#endif// common non-ascii keysenum{	TAB_KEY					= 0x09,	RETURN_KEY				= 0x0D,	ENTER_KEY				= 0x03,	ESCAPE_KEY				= 0x1B,	UP_ARROW_KEY			= 0x1E,	DOWN_ARROW_KEY			= 0x1F,	LEFT_ARROW_KEY			= 0x1C,	RIGHT_ARROW_KEY			= 0x1D,	BACKSPACE_KEY			= 0x08,	HELP_KEY				= 0x05,	PAGE_UP_KEY				= 0x0B,	PAGE_DOWN_KEY			= 0x0C,	HOME_KEY				= 0x01,	END_KEY					= 0x04,	CLEAR_KEY				= 0x1B,	FORWARD_DELETE_KEY		= 0x7F,		ANY_FUNCTION_KEY        = 0x10        	// n.b. all function keys give same code :-(};// undefined and quiet exceptionsenum{	kUnknownExceptionErr			= 999,		// catch all error code	kRuntimeLibException			= 998,		// runtime lib threw exception (bad alloc, etc)	kSilentErr						= -1,		// no alert message for this error	kMacZoopParamErr				= 950,		// NULL Ptr, Handle passed as parameter	kMacZoopParamErr1				= 951,		// Other parameter not correct	kMacZoopNULLPtrErr				= 952,		// NULL Ptr where it wasn't expected	kMacZoopLoadMenuErr				= 953,		// bad ref in MBAR resource	kMacZoopUnsupportedFeatureErr	= 954,		// requested feature not supported			kUnknownSignature				= '????',	kAppleIconColoursCLUTID			= 129};// current MacZoop version number & release stage:enum{	k_Version_Alpha					= 0,	k_Version_Beta,	k_Version_Final_Candidate,	k_Version_Release};#define	MACZOOP_VERSION						0x0250						// current version number#define	MACZOOP_VERSION_STAGE				k_Version_Release			// release status#define	MACZOOP_VERSION_NONRELEASE_NUM		0							// non-final stage#endif