/****************************************************************************************************			MacZoop - "the framework for the rest of us"		 ****			ZCustomDItemMaker.cpp			helper object to make custom dialog items**			****			© 2000, Graham Cox****************************************************************************************************/#include    "ZCustomDItemMaker.h"#include    "ProjectSettings.h"#include    "ZErrors.h"#if WIDGET_COLOUR_POPUP    #include    "ZCPopDialogItem.h"#endif#if WIDGET_STANDARD_LISTBOX    #include    "ZListDialogItem.h"#endif#if WIDGET_ICON_LISTBOX    #include    "ZIconListDialogItem.h"#endif#if WIDGET_TEXT_SCROLLER    #include    "ZTextDialogItem.h"#endif#if WIDGET_PROGRESS_BAR    #include    "ZPBarDialogItem.h"#endif#if WIDGET_GENERAL_SCROLLER    #include    "ZScrollerDialogItem.h"#endif#if WIDGET_GWORLD_SCROLLER    #include    "ZGWorldDialogItem.h"#endif#if WIDGET_CALENDAR_CONTROL    #include    "ZCalendarDialogItem.h"#endif    ZDialogItem*    ZCustomDItemMaker::MakeDialogItem( ZDialog* owner, short item, OSType magic ){    ZDialogItem*    di = NULL;        switch( magic )    {	#if WIDGET_COLOUR_POPUP    	case kMagicStringColourMenu:			FailNIL( di = new ZCPopDialogItem( owner, item ));			break;    #endif        #if WIDGET_STANDARD_LISTBOX		case kMagicStringListbox:			FailNIL( di = new ZListDialogItem( owner, item ));			break;	#endif	    #if WIDGET_TEXT_SCROLLER		case kMagicStringScrollbox:			FailNIL( di = new ZTextDialogItem( owner, item ));			break;    #endif        #if WIDGET_ICON_LISTBOX		case kMagicStringIconList:			FailNIL( di = new ZIconListDialogItem( owner, item ));			break;	#endif	    #if WIDGET_PROGRESS_BAR		case kMagicStringProgressBar:			FailNIL( di = new ZPBarDialogItem( owner, item ));			break;    #endif        #if WIDGET_GENERAL_SCROLLER		case kMagicStringGenericScroller:			FailNIL( di = new ZScrollerDialogItem( owner, item ));			break;    #endif        #if WIDGET_GWORLD_SCROLLER		case kMagicStringGWorldScroller:			FailNIL( di = new ZGWorldDialogItem( owner, item ));			break;	#endif		#if WIDGET_CALENDAR_CONTROL	    case kMagicStringCalendarItem:	        FailNIL( di = new ZCalendarDialogItem( owner, item ));	        break;		#endif					default:		    break;	}		return di;}