/****************************************************************************************************			MacZoop - "the framework for the rest of us"		 ****			ZMDEFWindow.h		-- a window that will display a menu. Handy for palettes*									that are created by tearing off a menu- the window*									can use the MDEF to display its contents.******			© 1999, Graham Cox*****************************************************************************************************/#pragma once#ifndef __ZMDEFWindow__#define	__ZMDEFWindow__#include	"ZWindow.h"class	ZMDEFWindow		: public ZWindow{protected:	MenuHandle		itsMenu;	Boolean			ownsMenu;	Handle			mdef;	short			item;		public:	ZMDEFWindow( ZCommander* aBoss, const short id, MenuHandle aMenu, Boolean becomeOwner = TRUE );	~ZMDEFWindow();		virtual void		InitZWindow();	virtual void		DrawContent();	virtual void		Click( const Point mouse, const short modifiers );		inline	MenuHandle	GetMenu() { return itsMenu; };	inline	short		GetChoice() { return item; };};enum{	kMsgNewWMenuItemChosen  = 'wmis'};#endif